 string fname;
 string lname;
 string haircolor;
 string eyecolor;
 int age;
 int weight;
 int height;
 string sex;
 string jobtitle;
 int salary;
 
 
 private void addNewAdult()
 {
  if (fname != null && lname != null && haircolor != null && eyecolor != null && sex != null && jobtitle != null)
  {
   Adult adult = new Adult()
   {
    FirstName = fname,
    LastName = lname,
    Age = age,
    EyeColor = eyecolor,
    HairColor = haircolor,
    Height = height,
    Id = age + height + weight,
    Sex = sex,
    Weight = weight,
    JobTitle = new Job()
    {
     JobTitle = jobtitle,
     Salary = salary,
    },
   };
   FileContext fileContext = new FileContext();
   fileContext.Adults.Add(adult);
   fileContext.SaveChanges();

  }
  else
  {
   ;
  }

 }

